#!/usr/bin/env pythonimport osimport sysimport subprocessimport shutilpwd = os.path.abspath(os.path.dirname(__file__))envdir = os.path.abspath(os.path.join(pwd, 'env'))if os.path.exists(envdir):    shutil.rmtree(envdir)ret = subprocess.call(['python', 'virtualenv.py', '--system-site-packages', envdir])if ret:     sys.exit(ret)if sys.platform == 'win32':    bin = 'Scripts'else:    bin = 'bin'ret = subprocess.call([os.path.join(envdir, bin, 'pip'), 'install',                       '--requirement', os.path.join(pwd, 'requirements/base.pip')])sys.exit(ret)